import React, { useState, useRef, ChangeEvent, KeyboardEvent, useEffect } from 'react';
import axios from 'axios';

// Types for letter status
type LetterStatus = 'correct' | 'present' | 'absent';

interface Letter {
  value: string;
  status: LetterStatus;
}

interface Guess {
  letters: Letter[];
}

// Constants
const WORD_LENGTH = 5;
const MAX_GUESSES = 6;
const RANDOM_WORD_API = 'https://api.api-ninjas.com/v1/randomword';
const API_KEY = 'IYg9EWn+jy/xf3SOrJlmJQ==Ek1bMBj7kZA99RZ2';

const App: React.FC = () => {
  const [targetWord, setTargetWord] = useState<string>('');
  const [guesses, setGuesses] = useState<Guess[]>([]);
  const [currentGuess, setCurrentGuess] = useState<string[]>(Array(WORD_LENGTH).fill(''));
  const [isGameOver, setIsGameOver] = useState<boolean>(false);
  const [isWinner, setIsWinner] = useState<boolean>(false);

  // Refs for input fields
  const inputRefs = useRef<Array<HTMLInputElement | null>>(Array(WORD_LENGTH).fill(null));

  // Fetch a random word when the component mounts
  useEffect(() => {
    fetchRandomWord();
  }, []);

  const fetchRandomWord = async () => {
    try {
      const response = await axios.get('https://random-word-api.vercel.app/api?words=1&length=5', {
      });

      const word = response.data.word[0].toUpperCase();
      if (word.length === WORD_LENGTH) {
        setTargetWord(word);
      } else {
        // Retry if the word is not 5 letters
        fetchRandomWord();
      }
    } catch (error) {
      console.error('Error fetching word:', error);
      alert('Failed to load a random word. Please try again.');
    }
  };

  // Handle input changes and move focus to the next box
  const handleChange = (index: number, e: ChangeEvent<HTMLInputElement>) => {
    const value = e.target.value.toUpperCase();
    if (/^[A-Z]?$/.test(value)) {
      const newGuess = [...currentGuess];
      newGuess[index] = value;
      setCurrentGuess(newGuess);

      if (value && index < WORD_LENGTH - 1) {
        inputRefs.current[index + 1]?.focus();
      }
    }
  };

  const handleKeyDown = (index: number, e: KeyboardEvent<HTMLInputElement>) => {
    if (e.key === 'Backspace' && !currentGuess[index] && index > 0) {
      inputRefs.current[index - 1]?.focus();
    }
    if (e.key === 'Enter') handleSubmit();
  };

  const handleSubmit = () => {
    const guessString = currentGuess.join('');
    if (guessString.length !== WORD_LENGTH) {
      alert('Please complete your guess.');
      return;
    }

    const newGuess: Guess = {
      letters: currentGuess.map((char, index) => ({
        value: char,
        status: getLetterStatus(char, index),
      })),
    };

    const newGuesses = [...guesses, newGuess];
    setGuesses(newGuesses);
    setCurrentGuess(Array(WORD_LENGTH).fill(''));

    if (guessString === targetWord) {
      setIsWinner(true);
      setIsGameOver(true);
    } else if (newGuesses.length >= MAX_GUESSES) {
      setIsGameOver(true);
    }
  };

  const getLetterStatus = (char: string, index: number): LetterStatus => {
    if (targetWord[index] === char) return 'correct';
    if (targetWord.includes(char)) return 'present';
    return 'absent';
  };

  const handleRestart = () => {
    setGuesses([]);
    setCurrentGuess(Array(WORD_LENGTH).fill(''));
    setIsGameOver(false);
    setIsWinner(false);
    fetchRandomWord(); // Fetch a new word
    inputRefs.current[0]?.focus();
  };

  return (
    <div style={{ textAlign: 'center', marginTop: '50px' }}>
      <h1>Wordle Clone</h1>

      {guesses.map((guess, i) => (
        <div key={i} style={{ margin: '10px 0' }}>
          {guess.letters.map((letter, j) => (
            <span
              key={j}
              style={{
                display: 'inline-block',
                width: '30px',
                height: '30px',
                lineHeight: '30px',
                border: '1px solid black',
                margin: '0 5px',
                backgroundColor:
                  letter.status === 'correct'
                    ? 'green'
                    : letter.status === 'present'
                    ? 'yellow'
                    : 'gray',
                color: letter.status === 'present' ? 'black' : 'white',
                textTransform: 'uppercase',
              }}
            >
              {letter.value}
            </span>
          ))}
        </div>
      ))}

      {!isGameOver && (
        <div style={{ margin: '10px 0' }}>
          {currentGuess.map((_, index) => (
            <input
              key={index}
              type="text"
              maxLength={1}
              value={currentGuess[index]}
              onChange={(e) => handleChange(index, e)}
              onKeyDown={(e) => handleKeyDown(index, e)}
              ref={(el) => (inputRefs.current[index] = el)}
              style={{
                width: '30px',
                height: '30px',
                textAlign: 'center',
                margin: '0 5px',
                textTransform: 'uppercase',
              }}
            />
          ))}
        </div>
      )}

      {!isGameOver && (
        <button onClick={handleSubmit} style={{ marginTop: '10px' }}>
          Submit
        </button>
      )}

      {isGameOver && (
        <div>
          <p>
            {isWinner
              ? 'Congratulations! You guessed the word!'
              : `Game Over! The word was: ${targetWord}`}
          </p>
          <button onClick={handleRestart}>Restart</button>
        </div>
      )}
    </div>
  );
};

export default App;
