import React, { useState, useEffect } from 'react';
import axios from 'axios';
import InputRow from './InputRow';
import GuessRow from './GuessRow';

const RANDOM_WORD_API = 'https://random-word-api.vercel.app/api?words=1&length=5';
const WORD_LENGTH = 5;
const MAX_GUESSES = 6;

const Container: React.FC = () => {
    const [targetWord, setTargetWord] = useState<string>('');
    const [guesses, setGuesses] = useState<string[]>([]);
    const [currentGuess, setCurrentGuess] = useState<string[]>(Array(WORD_LENGTH).fill(''));
    const [isGameOver, setIsGameOver] = useState(false);
    const [isWinner, setIsWinner] = useState(false);

    useEffect(() => {
      fetchRandomWord();
    }, []);


  const fetchRandomWord = async () => {
    try {
      const response = await axios.get(RANDOM_WORD_API);
      const word = response.data[0];
      setTargetWord(word);
    } catch (error) {
      console.error('Error fetching word:', error);
      alert('Failed to load word. Try again.');
    }
  };

  const handleSubmit = () => {
    const guess = currentGuess.join('');
    if (guess.length !== WORD_LENGTH) return;
    setGuesses([...guesses, guess]);

    if (guess === targetWord) {
      setIsWinner(true);
      setIsGameOver(true);
    } else if (guesses.length + 1 >= MAX_GUESSES) {
      setIsGameOver(true);
    }

    setCurrentGuess(Array(WORD_LENGTH).fill(''));
  };

  const handleRestart = () => {
    setGuesses([]);
    setCurrentGuess(Array(WORD_LENGTH).fill(''));
    setIsWinner(false);
    setIsGameOver(false);
    fetchRandomWord();
  };

  return (
    <div className="flex flex-col items-center mt-10">
      <h1 className="text-3xl font-bold mb-5">Wordle Clone</h1>
      <div className="space-y-3 mb-5">
        {guesses.map((guess, index) => (
          <GuessRow key={index} word={guess} targetWord={targetWord} />
        ))}
      </div>
      {!isGameOver && (
        <InputRow
          currentGuess={currentGuess}
          setCurrentGuess={setCurrentGuess}
          onSubmit={handleSubmit}
        />
      )}
      {isGameOver && (
        <div className="mt-5">
          <p>{isWinner ? 'You won!' : `Game Over! The word was ${targetWord}`}</p>
          <button
            className="mt-3 px-4 py-2 bg-blue-500 text-white rounded"
            onClick={handleRestart}
          >
            Restart
          </button>
        </div>
      )}
    </div>
  );
};

export default Container;
