import React, { useState, useRef, ChangeEvent, KeyboardEvent } from 'react';

// Types for letter status
type LetterStatus = 'correct' | 'present' | 'absent';

interface Letter {
  value: string;
  status: LetterStatus;
}

interface Guess {
  letters: Letter[];
}

// Constants
const WORD_LENGTH = 5;
const MAX_GUESSES = 6;
const TARGET_WORD = 'APPLE'; // Hardcoded for now, can be randomized later.

const App: React.FC = () => {
  const [guesses, setGuesses] = useState<Guess[]>([]);
  const [currentGuess, setCurrentGuess] = useState<string[]>(Array(WORD_LENGTH).fill(''));
  const [isGameOver, setIsGameOver] = useState<boolean>(false);
  const [isWinner, setIsWinner] = useState<boolean>(false);

  // Refs for individual input boxes
  const inputRefs = useRef<Array<HTMLInputElement | null>>(Array(WORD_LENGTH).fill(null));

  // Handle input change and automatically move focus to the next box
  const handleChange = (index: number, e: ChangeEvent<HTMLInputElement>) => {
    const value = e.target.value.toUpperCase();
    if (/^[A-Z]?$/.test(value)) { // Allow only letters
      const newGuess = [...currentGuess];
      newGuess[index] = value;
      setCurrentGuess(newGuess);

      // Move focus to the next input
      if (value && index < WORD_LENGTH - 1) {
        inputRefs.current[index + 1]?.focus();
      }
    }
  };

  // Handle backspace to move to the previous input
  const handleKeyDown = (index: number, e: KeyboardEvent<HTMLInputElement>) => {
    if (e.key === 'Backspace' && !currentGuess[index] && index > 0) {
      inputRefs.current[index - 1]?.focus();
    }
    if (e.key === 'Enter') {
      handleSubmit();
    }
  };

  const handleSubmit = () => {
    const guessString = currentGuess.join('');
    if (guessString.length !== WORD_LENGTH) {
      alert('Please complete your guess.');
      return;
    }

    const newGuess: Guess = {
      letters: currentGuess.map((char, index) => ({
        value: char,
        status: getLetterStatus(char, index),
      })),
    };

    const newGuesses = [...guesses, newGuess];
    setGuesses(newGuesses);
    setCurrentGuess(Array(WORD_LENGTH).fill(''));

    if (guessString === TARGET_WORD) {
      setIsWinner(true);
      setIsGameOver(true);
    } else if (newGuesses.length >= MAX_GUESSES) {
      setIsGameOver(true);
    }
  };

  const getLetterStatus = (char: string, index: number): LetterStatus => {
    if (TARGET_WORD[index] === char) return 'correct';
    if (TARGET_WORD.includes(char)) return 'present';
    return 'absent';
  };

  const handleRestart = () => {
    setGuesses([]);
    setCurrentGuess(Array(WORD_LENGTH).fill(''));
    setIsGameOver(false);
    setIsWinner(false);
    inputRefs.current[0]?.focus(); // Focus on the first input
  };

  return (
    <div style={{ textAlign: 'center', marginTop: '50px' }}>
      <h1>Wordle Clone</h1>

      {guess.letters.map((letter, j) => (
  <span
    key={j}
    style={{
      display: 'inline-block',
      width: '30px',
      height: '30px',
      lineHeight: '30px',
      border: '1px solid black',
      margin: '0 5px',
      textTransform: 'uppercase',
      backgroundColor:
        letter.status === 'correct'
          ? 'green'
          : letter.status === 'present'
          ? 'yellow'
          : 'gray',
      color:
        letter.status === 'present'
          ? 'black' // Black text on yellow background
          : 'white', // White text on other backgrounds
    }}
  >
    {letter.value}
  </span>
))}

      {!isGameOver && (
        <div style={{ margin: '10px 0' }}>
          {currentGuess.map((_, index) => (
            <input
              key={index}
              type="text"
              maxLength={1}
              value={currentGuess[index]}
              onChange={(e) => handleChange(index, e)}
              onKeyDown={(e) => handleKeyDown(index, e)}
              ref={(el) => (inputRefs.current[index] = el)}
              style={{
                width: '30px',
                height: '30px',
                textAlign: 'center',
                margin: '0 5px',
                textTransform: 'uppercase',
              }}
            />
          ))}
        </div>
      )}

      {!isGameOver && (
        <button onClick={handleSubmit} style={{ marginTop: '10px' }}>
          Submit
        </button>
      )}

      {isGameOver && (
        <div>
          <p>
            {isWinner
              ? 'Congratulations! You guessed the word!'
              : `Game Over! The word was: ${TARGET_WORD}`}
          </p>
          <button onClick={handleRestart}>Restart</button>
        </div>
      )}
    </div>
  );
};

export default App;
